mapper.sql:
SELECT T.*, T.INVE_AMOUNT / 10000 || ' 万元 人民币' as INVE_AMOUNT_DISPLAY, T.TOTAL_FINANCIAL / 10000 || ' 万元 人民币' as INVE_CURRENCY_DISPLAY, I.INVESTOR_NAME, (CASE T.INVE_ROLE WHEN '3' THEN '2' ELSE I.INVESTOR_TYPE END) AS INVESTOR_TYPE,
CASE WHEN nvl(T.INVE_AMOUNT, 0) = 0 THEN 0 ELSE ROUND((T.TOTAL_FINANCIAL / T.INVE_AMOUNT) * 100, 2) END FINANCIALPROCESS, ROUND(T.INVE_AMOUNT / (SELECT SUM(T1.INVE_AMOUNT) FROM RZ_T_FUND_SHARE_INFO T1 LEFT JOIN BD_T_INVE_INFO I ON T1.INVESTOR_ID = I.INVESTOR_ID WHERE T1.STATUS = '0' AND T1.FUND_ID = T.FUND_ID and FUND_ID = ${paramMap.FUND_ID} and CLOSE_ROUND = (SELECT MAX(T0.CLOSE_ROUND) FROM RZ_T_FUND_SHARE_INFO T0 WHERE T0.FUND_ID = ${paramMap.FUND_ID} AND T0.STATUS = '0')
) * 100, 2) || '%' as GB, (SELECT SUM(T2.INVE_AMOUNT) FROM RZ_T_FUND_SHARE_INFO T2 WHERE 1 = 1 AND T2.STATUS = '0' and FUND_ID = ${paramMap.FUND_ID} and CLOSE_ROUND = (SELECT MAX(T0.CLOSE_ROUND) FROM RZ_T_FUND_SHARE_INFO T0 WHERE T0.FUND_ID = ${paramMap.FUND_ID} AND T0.STATUS = '0')
) AS SUM_INVE_AMOUNT FROM RZ_T_FUND_SHARE_INFO T LEFT JOIN BD_T_INVE_INFO I ON T.INVESTOR_ID = I.INVESTOR_ID
and FUND_ID = ${paramMap.FUND_ID} and CLOSE_ROUND = (SELECT MAX(T0.CLOSE_ROUND) FROM RZ_T_FUND_SHARE_INFO T0 WHERE T0.FUND_ID = ${paramMap.FUND_ID} AND T0.STATUS = '0')

SELECT DISTINCT c.INVESTOR_NAME,d.FUND_NAME,a.* FROM RZ_T_FUND_SHARE_INFO a left join BD_T_INVE_INFO c on a.INVESTOR_ID=c.INVESTOR_ID left join BD_T_FUND_INFO d on d.FUND_ID=a.FUND_ID
c.status='0'and a.status='0' and d.status='1'
AND a.FUND_ID= #{searchBean.fundId,jdbcType=VARCHAR}
AND c.INVESTOR_NAME like #{searchBean.investorName}

